mapper.update:
update organization_relation
set org_rlt_info_id = #{orgRltInfoId,jdbcType=INTEGER},
  org_rlt_duty_id = #{orgRltDutyId,jdbcType=INTEGER},
  org_rlt_user_id = #{orgRltUserId,jdbcType=INTEGER},
  org_rlt_join_time = #{orgRltJoinTime,jdbcType=TIMESTAMP}
where org_rlt_id = #{orgRltId,jdbcType=INTEGER}

mapper.select:
select _or.org_rlt_id orgRltId, oi.org_info_name infoName, oi.org_info_manager_region orgInfoManagerRegion, oi.org_info_active_region orgInfoActionRegion, oi.org_info_describe orgInfoDescribe, oi.org_info_parent_id orgInfoParentId, od.org_duty_name orgDutyName, od.org_duty_describe orgDutyDescribe, ot.org_type_name orgTypeName, od.org_duty_parent_id orgDutyParentId, bu.name buName, bu.id_card buIdCard, bu.sex buSex, bu.address buAddress, bu.edu_level buEduLevel, bu.phone buPhone, bu.nation buNation, bu.email buEmail, bu.qq buQq, bu.wechat buWechat, bu.head_img buHeadImg, bu.political_bg buPoliticalBg from organization_relation _or inner join organization_info oi inner join organization_duty od inner join base_user bu inner join organization_type ot where _or.org_rlt_info_id = oi.org_info_id and _or.org_rlt_duty_id = od.org_duty_id and _or.org_rlt_user_id = bu.uid and od.org_duty_type_id = ot.org_type_id and _or.org_rlt_info_id = #{orgRltInfoId}
and _or.org_rlt_duty_id = #{orgRltDutyId}
and bu.sex = #{buSex}

select _or.org_rlt_id orgRltId, oi.org_info_id orgInfoId, ot.org_type_id orgTypeId, od.org_duty_id orgDutyId, od.org_duty_parent_id orgDutyParentId, bui.base_user_id baseUserId, pui.party_user_id partyUserId, bui.name name, bui.sex sex, nt.name nationName, bui.birth_date birthDate, el.name educationName, adl.name academicDegreeName, od.org_duty_name orgDutyName, pui.type partyType, pui.status partyStatus, bui.id_photo idPhoto, bui.mobile_phone mobilePhone, _or.org_rlt_join_time orgRltJoinTime, bui.id_card idCard from organization_relation _or inner join organization_information oi on _or.org_rlt_info_id = oi.org_info_id inner join organization_type ot on oi.org_info_type_id = ot.org_type_id inner join organization_duty od on _or.org_rlt_duty_id = od.org_duty_id inner join base_user_info bui on _or.org_rlt_user_id = bui.base_user_id left join party_user_info pui on bui.base_user_id = pui.party_user_id left join nation_type nt on bui.nation = nt.nt_id left join education_level el on bui.education = el.edu_level_eid left join academic_degree_level adl on bui.academic_degree = adl.ad_d_aid where 1 = 1 and _or.org_rlt_info_id = #{orgRltInfoId}
and od.org_duty_parent_id = #{orgDutyParentId}
and _or.org_rlt_user_id = #{orgRltUserId}
and _or.org_rlt_id = #{orgRltId}
and _or.org_rlt_join_time >= #{orgRltStartJoinTime}
and _or.org_rlt_join_time <= #{orgRltEndJoinTime}
and bui.sex = #{sex}
and bui.id_card = #{idCard}

select distinct _or.org_rlt_user_id orgRltUserId from organization_relation _or inner join base_user_info bui on _or.org_rlt_user_id = bui.base_user_id where 1 = 1 and _or.org_rlt_info_id = #{orgRltInfoId}
and _or.org_rlt_join_time >= #{orgRltStartJoinTime}
and _or.org_rlt_join_time <= #{orgRltEndJoinTime}
and _or.org_rlt_user_id = #{orgRltUserId}
and bui.sex = #{sex}
